import React, { useState, useEffect } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';

const BrandDetails = ({ brands }) => {
  const handleEditClick = async (currentBrand, newBrandValue) => {
    try {
      const response = await fetch(`http://localhost:1337/products/brand/${currentBrand}/${newBrandValue}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify({ newBrand: newBrandValue }),
      });

      if (response.ok) {
        console.log('Brand updated successfully');
      } else {
        console.error('Failed to update brand');
      }
    } catch (error) {
      console.error('Error updating brand:', error.message);
    }
  };

  return (
    <div className='brand-details'>
      <h3>BRANDS</h3>
      <table>
        <thead>
          <tr>
            <th>Brand</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {brands.map((brand, index) => (
            <tr key={index}>
              <td>{brand}</td>
              <td>
                <input type='text' placeholder='Enter new brand' />
              </td>
              <td>
                <button onClick={() => handleEditClick(brand, /* pass new brand value here */)}>Edit</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BrandDetails;
