import React, { useState } from 'react';
import { useProductsContext } from '../hooks/useProductContext';
import { useAuthContext } from '../hooks/useAuthContext';

const ProductDetails = ({ product }) => {
  const { dispatch } = useProductsContext();
  const { user } = useAuthContext();
  const [quantity, setQuantity] = useState(product.number);
  const [editedName, setEditedName] = useState(product.name);
  const [editedBrand, setEditedBrand] = useState(product.brand);
  const [editedCategory, setEditedCategory] = useState(product.category);
  const [editedImage, setEditedImage] = useState(null); // State for edited image

  const handleEditClick = async () => {
    if (!user) {
      return;
    }
    try {
      const formData = new FormData();
      formData.append('name', editedName);
      formData.append('brand', editedBrand);
      formData.append('category', editedCategory);
      formData.append('number', quantity);
      if (image) {
        formData.append('image', image);
      }
      
      const response = await fetch(`http://localhost:1337/products/${product._id}`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${user.token}`
        },
        body: formData
      });
      
      const json = await response.json();
  
      if (response.ok) {
        dispatch({ type: 'UPDATE_PRODUCT', payload: json.updatedProduct });
        console.log("Product updated successfully");
      }
    } catch (error) {
      console.error(error.message);
      // Handle error appropriately, e.g., show an error message to the user
    }
};

  // Handler for updating the quantity state
  const handleQuantityChange = (event) => {
    const value = parseInt(event.target.value);
    setQuantity(value >= 1 ? value : 1); // Ensure quantity is at least 1
  };

  // Handler for updating the image state
  const handleImageChange = (event) => {
    const file = event.target.files[0];
    setEditedImage(file);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0]; // Ensure that the file object is properly obtained
    setImage(file); // Update the image state with the selected file
    console.log('File:', file);
    const reader = new FileReader();

    reader.onload = () => {
        setImagePreview(reader.result); // Optionally, you can update an image preview state for displaying the selected image
    };

    reader.readAsDataURL(file); // Make sure the file is read as data URL
};

  return (
    <div className='product-details'>
      <p><strong>Name:</strong>
        <input
          type="text"
          value={editedName}
          onChange={(e) => setEditedName(e.target.value)}
        />
      </p>
      <p><strong>Brand:</strong>
        <input
          type="text"
          value={editedBrand}
          onChange={(e) => setEditedBrand(e.target.value)}
        />
      </p>
      <p><strong>Category:</strong>
        <input
          type="text"
          value={editedCategory}
          onChange={(e) => setEditedCategory(e.target.value)}
        />
      </p>
      <p><strong>Image:</strong></p>
      <img src={editedImage ? URL.createObjectURL(editedImage) : product.image} alt="Product Image" className="product-image" />
      <input type="file" accept="image/*" onChange={handleImageChange} />
      <p><strong>Number:</strong>
        <input
          type="number"
          value={quantity}
          onChange={handleQuantityChange}
          min="1"
          step="1"
        />
      </p>
      <button onClick={handleEditClick}>Save Changes</button>
    </div>
  );
};

export default ProductDetails;
