import React, { useState, useEffect } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';

const BrandDetails = ({ brands }) => {
  const { user } = useAuthContext();
  const [editedBrands, setEditedBrands] = useState(brands);

  const handleEditClick = async (brandName, newBrandName) => {
    try {
      const response = await fetch(`http://localhost:1337/products/brand/${brandName}/${newBrandName}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
      });

      if (response.ok) {
        // Update the brand list if the request is successful
        setEditedBrands((prevBrands) =>
          prevBrands.map((b) => (b === brandName ? newBrandName : b))
        );
        console.log('Brand updated successfully');
      } else {
        console.error('Failed to update brand');
      }
    } catch (error) {
      console.error('Error updating brand:', error.message);
    }
  };

  return (
    <div className='brand-details'>
      <h3>BRANDS</h3>
      <table>
        <thead>
          <tr>
            <th>Brand</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {editedBrands.map((brand) => (
            <tr key={brand}>
              <td>
                {brand}
              </td>
              <td>
                <input
                  type='text'
                  defaultValue={brand} // Use defaultValue instead of value
                  onChange={(e) => {
                    const newBrandName = e.target.value;
                    handleEditClick(brand, newBrandName);
                  }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BrandDetails;
