import React, { useState } from 'react';
import { useProductsContext } from '../hooks/useProductContext';
import { useAuthContext } from '../hooks/useAuthContext';

const ProductDetails = ({ product }) => {
  const { dispatch } = useProductsContext();
  const { user } = useAuthContext();
  const [fields, setFields] = useState({
    name: product.name,
    brand: product.brand,
    category: product.category,
    image: product.image,
    number: product.number
  });

  const handleEditClick = async () => {
    if (!user) {
      return;
    }
    try {
      const response = await fetch(`http://localhost:1337/products/${product._id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify(fields)
      });
  
      if (response.ok) {
        const updatedProduct = await response.json();
        dispatch({ type: 'UPDATE_PRODUCT', payload: updatedProduct });
        console.log("Product details updated successfully");
      }
    } catch (error) {
      console.error(error.message);
      // Handle error appropriately, e.g., show an error message to the user
    }
  };

  const handleChange = (field, value) => {
    setFields({ ...fields, [field]: value });
  };

  return (
    <div className='product-details'>
      <p><strong>Name:</strong> <input type="text" value={fields.name} onChange={(e) => handleChange('name', e.target.value)} /></p>
      <p><strong>Brand:</strong> <input type="text" value={fields.brand} onChange={(e) => handleChange('brand', e.target.value)} /></p>
      <p><strong>Category:</strong> <input type="text" value={fields.category} onChange={(e) => handleChange('category', e.target.value)} /></p>
      <p><strong>Image:</strong> <input type="text" value={fields.image} onChange={(e) => handleChange('image', e.target.value)} /></p>
      <img src={fields.image} alt="Product Image" className="product-image" />
      <p><strong>Number:</strong> <input type="number" value={fields.number} onChange={(e) => handleChange('number', e.target.value)} /></p>
      {user && (user.role === 'administrator' || user.role === 'moderator') && (
        <div>
          <button onClick={handleEditClick}>Save Changes</button>
        </div>
      )}
    </div>
  );
};

export default ProductDetails;
