import React, { useState } from 'react';
import { useProductsContext } from '../hooks/useProductContext';
import { useAuthContext } from '../hooks/useAuthContext';

const ProductDetails = ({ product }) => {
  const { dispatch } = useProductsContext();
  const { user } = useAuthContext();
  const [quantity, setQuantity] = useState(product.number);
  const [editedName, setEditedName] = useState(product.name);
  const [editedBrand, setEditedBrand] = useState(product.brand);
  const [editedCategory, setEditedCategory] = useState(product.category);
  const [image, setImage] = useState(null); // State to hold the selected image file

  const handleEditClick = async () => {
    if (!user) {
      return;
    }
    try {
      const updates = {
        name: editedName,
        brand: editedBrand,
        category: editedCategory,
        number: quantity,
        image: await getBase64Image(image), // Convert selected image to base64 before updating
      };

      const response = await fetch(`http://localhost:1337/products/${product._id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify(updates),
      });
      const json = await response.json();

      if (response.ok) {
        dispatch({ type: 'UPDATE_PRODUCT', payload: json });
        console.log('Product updated successfully');
      }
    } catch (error) {
      console.error(error.message);
      // Handle error appropriately
    }
  };

  const handleQuantityChange = (event) => {
    const value = parseInt(event.target.value);
    setQuantity(value >= 1 ? value : 1);
  };

  // Function to convert selected image file to base64 format
  const getBase64Image = async (image) => {
    return new Promise((resolve, reject) => {
      if (!image) {
        resolve('');
        return;
      }

      const reader = new FileReader();
      reader.onload = () => {
        resolve(reader.result);
      };
      reader.onerror = (error) => {
        reject(error);
      };

      reader.readAsDataURL(image);
    });
  };

  // Function to handle file change
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
  };

  return (
    <div className="product-details">
      <p>
        <strong>Name:</strong>
        <input
          type="text"
          value={editedName}
          onChange={(e) => setEditedName(e.target.value)}
        />
      </p>
      <p>
        <strong>Brand:</strong>
        <input
          type="text"
          value={editedBrand}
          onChange={(e) => setEditedBrand(e.target.value)}
        />
      </p>
      <p>
        <strong>Category:</strong>
        <input
          type="text"
          value={editedCategory}
          onChange={(e) => setEditedCategory(e.target.value)}
        />
      </p>
      <p>
        <strong>Number:</strong>
        <input
          type="number"
          value={quantity}
          onChange={handleQuantityChange}
          min="1"
          step="1"
        />
      </p>
      <p>
        <strong>Image:</strong>
        
        <input type="file" accept="image/*" onChange={handleFileChange} />
        {image && <img src={URL.createObjectURL(image)} alt="Selected" style={{ width: 100, height: 100 }} />}
      </p>
      <button onClick={handleEditClick}>Save Changes</button>
    </div>
  );
};

export default ProductDetails;
