import React, { useState } from 'react';
import { useProductsContext } from '../hooks/useProductContext';
import { useAuthContext } from '../hooks/useAuthContext';

const ProductDetails = ({ product }) => {
  const { dispatch } = useProductsContext();
  const { user } = useAuthContext();
  const [quantity, setQuantity] = useState(1); // State for quantity input field

  const handleBuyClick = () => {
    // Handle buy functionality here with the selected quantity
    console.log('Buy clicked with quantity:', quantity);
  };

  const handleDeleteClick = async () => {
    if (!user) {
      return;
    }
    try {
      console.log("Product ID to delete:", product._id); // Check if correct ID is being sent
      const response = await fetch(`http://localhost:1337/products/deleting/${product._id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${user.token}`
        }
      });
      const json = await response.json();

      if (response.ok) {
        dispatch({ type: 'DELETE_PRODUCT', payload: product }); // Pass the product itself as payload
      }
    } catch (error) {
      console.error(error.message);
      // Handle error appropriately, e.g., show an error message to the user
    }
  };

  // Handler for updating the quantity state
  const handleQuantityChange = (event) => {
    const value = parseInt(event.target.value);
    setQuantity(value >= 1 ? value : 1); // Ensure quantity is at least 1
  };

  return (
    <div className='product-details'>
      <p><strong>Name:</strong> {product.name}</p>
      <p><strong>Brand:</strong> {product.brand}</p>
      <p><strong>Category:</strong> {product.category}</p>
      <p><strong>Image:</strong></p>
      <img src={product.image} alt="Product Image" className="product-image" />
      <p><strong>Number:</strong>{product.number}</p>
      {user && user.role === 'basic' && (
        <div>
          <input 
            type="number"
            value={quantity}
            onChange={handleQuantityChange}
            min="1"
            step="1"
          />
          <button onClick={handleBuyClick}>Buy</button>
        </div>
      )}
      {user && (user.role === 'administrator' || user.role === 'moderator') && (
        <span onClick={handleDeleteClick}>DELETE</span>
      )}
    </div>
  );
};

export default ProductDetails;
