import React, { useEffect, useState } from "react";
import { useAuthContext } from "../hooks/useAuthContext";
import ProductDetails from '../components/ProductDetails';
import AddProduct from '../components/AddProduct';
import UserDetails from '../components/UserDetails';
import AddUser from '../components/AddUser';
import BrandDetails from '../components/BrandDetails';
import CategoryDetails from '../components/CategoryDetails'; // Import CategoryDetails
import { useProductsContext } from "../hooks/useProductContext";
import io from 'socket.io-client'; // Import Socket.IO client

const Dashboard = () => {
    const { products, dispatch } = useProductsContext();
    const { user } = useAuthContext();
    const [users, setUsers] = useState([]);
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]); // State for categories
    const [socketId, setSocketId] = useState(null); // State to store socket ID

    useEffect(() => {
        const socket = io('http://localhost:1337'); // Connect to Socket.IO server

        // Listen for 'socketId' event and update state with received socket ID
        socket.on('socketId', (id) => {
            console.log('Socket ID:', id);
            setSocketId(id);
        });

        return () => {
            socket.disconnect(); // Disconnect Socket.IO when component unmounts
        };
    }, []);

    
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('http://localhost:1337/products', {
                    headers: {
                        'Authorization': `Bearer ${user.token}`
                    }
                });
                const data = await response.json();

                if (response.ok) {
                    dispatch({ type: 'GET_PRODUCTS', payload: data });
                }
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        if (user) {
            fetchProducts();
        }
    }, [dispatch, user]);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await fetch('http://localhost:1337/users', {
                    headers: {
                        'Authorization': `Bearer ${user.token}`
                    }
                });
                const data = await response.json();

                if (response.ok) {
                    setUsers(data);
                }
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        if (user) {
            fetchUsers();
        }
    }, [user]);

    useEffect(() => {
        const fetchBrands = async () => {
            try {
                const response = await fetch('http://localhost:1337/products/brands', {
                    headers: {
                        'Authorization': `Bearer ${user.token}`
                    }
                });
                const data = await response.json();

                if (response.ok) {
                    setBrands(data); // Update brands state with fetched data
                }
            } catch (error) {
                console.error('Error fetching brands:', error);
            }
        };

        if (user) {
            fetchBrands();
        }
    }, [user]);

    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await fetch('http://localhost:1337/products/categories', {
                    headers: {
                        'Authorization': `Bearer ${user.token}`
                    }
                });
                const data = await response.json();

                if (response.ok) {
                    setCategories(data); // Update categories state with fetched data
                }
            } catch (error) {
                console.error('Error fetching categories:', error);
            }
        };

        if (user) {
            fetchCategories();
        }
    }, [user]);

    return (
        <div>
            <div>
                <h3>PRODUCTS</h3>
                {products && products.map((product) => (
                    <ProductDetails key={product._id} product={product} />
                ))}
            </div>
            {user && user.role === 'administrator' && (
                <>
                    <AddProduct />
                    <BrandDetails brands={brands} />
                    <CategoryDetails categories={categories} />
                    <AddUser />
                    <UserDetails users={users.map((user) => ({ ...user, key: user._id }))} />
                </>
            )}
            {user && user.role === 'moderator' && (
                <>
                    <AddProduct />
                    <BrandDetails brands={brands} />
                    <CategoryDetails categories={categories} />
                </>
            )}
        </div>
    );
};

export default Dashboard;
