import React, { useState } from 'react';
import { useProductsContext } from '../hooks/useProductContext';
import { useAuthContext } from '../hooks/useAuthContext';

const ProductDetails = ({ product }) => {
  const { dispatch } = useProductsContext();
  const { user } = useAuthContext();
  const [quantity, setQuantity] = useState(product.number);
  const [editedName, setEditedName] = useState(product.name);
  const [editedBrand, setEditedBrand] = useState(product.brand);
  const [editedCategory, setEditedCategory] = useState(product.category);

  const handleEditClick = async (field) => {
    if (!user) {
      return;
    }
    try {
      let editedValue;
      let fieldName;
      switch (field) {
        case 'name':
          editedValue = editedName;
          fieldName = 'name';
          break;
        case 'brand':
          editedValue = editedBrand;
          fieldName = 'brand';
          break;
        case 'category':
          editedValue = editedCategory;
          fieldName = 'category';
          break;
        case 'number':
          editedValue = quantity;
          fieldName = 'number';
          break;
        default:
          return;
      }
  
      // Fetch the product's _id
      const productId = product._id;
  
      // Prepare the data for PATCH request
      const data = {
        [fieldName]: editedValue
      };
  
      // Send PATCH request to update the specific field
      const response = await fetch(`http://localhost:1337/products/${productId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify(data)
      });
  
      const json = await response.json();
  
      if (response.ok) {
        dispatch({ type: 'UPDATE_PRODUCT', payload: json });
        console.log(`Product ${field} updated successfully`);
      }
    } catch (error) {
      console.error(error.message);
      // Handle error appropriately, e.g., show an error message to the user
    }
  };
  

  const handleQuantityChange = (event) => {
    const value = parseInt(event.target.value);
    setQuantity(value >= 1 ? value : 1); // Ensure quantity is at least 1
  };

  return (
    <div className='product-details'>
      <p><strong>Name:</strong> 
        <input 
          type="text"
          value={editedName}
          onChange={(e) => setEditedName(e.target.value)}
        />
        <button onClick={() => handleEditClick('name')}>Edit</button>
      </p>
      <p><strong>Brand:</strong>
        <input 
          type="text"
          value={editedBrand}
          onChange={(e) => setEditedBrand(e.target.value)}
        />
        <button onClick={() => handleEditClick('brand')}>Edit</button>
      </p>
      <p><strong>Category:</strong>
        <input 
          type="text"
          value={editedCategory}
          onChange={(e) => setEditedCategory(e.target.value)}
        />
        <button onClick={() => handleEditClick('category')}>Edit</button>
      </p>
      <p><strong>Number:</strong>
        <input 
          type="number"
          value={quantity}
          onChange={handleQuantityChange}
          min="1"
          step="1"
        />
        <button onClick={() => handleEditClick('number')}>Edit</button> 
      </p>
    </div>
  );
};

export default ProductDetails;
