import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { useAuthContext } from './hooks/useAuthContext';

import Navbar from './components/Navbar';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Dashboard from './pages/Dashboard';

function App() {
  const { user } = useAuthContext();

  return (
    <div className="App">
      <BrowserRouter>
        <Navbar />
        <div className='pages'>
          <Routes>
            {/* Redirect logged-in users from /login and /signup to /dashboard */}
            <Route path="/" element={<Navigate to={user ? '/dashboard' : '/login'} />} />
            <Route path="/signup" element={<Navigate to={user ? '/dashboard' : '/signup'} />} />
            {/* Redirect unauthorized users from /dashboard to / */}
            <Route path="/dashboard" element={user ? <Dashboard /> : <Navigate to="/" />} />
            {/* Allow unauthenticated users to access /login and /signup */}
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
          </Routes>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
