import React, { useState, useEffect } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';

const BrandDetails = ({ brands }) => {
  const { user } = useAuthContext();
  const [editedBrands, setEditedBrands] = useState(brands);
  const [newBrandValue, setNewBrandValue] = useState('');

  // Update editedBrands state whenever the brands prop changes
  useEffect(() => {
    setEditedBrands(brands);
  }, [brands]);

  const handleEditClick = async (currentBrand) => {
    try {
      const response = await fetch(`http://localhost:1337/products/brand/${currentBrand}/${newBrandValue}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify({ newBrand: newBrandValue }),
      });

      if (response.ok) {
        // Update the brand list if the request is successful
        setEditedBrands((prevBrands) =>
          prevBrands.map((b) => (b === currentBrand ? newBrandValue : b))
        );
        console.log('Brand updated successfully');
      } else {
        console.error('Failed to update brand');
      }
    } catch (error) {
      console.error('Error updating brand:', error.message);
    }
  };

  const handleDeleteClick = async (brand) => {
    try {
      const response = await fetch(`http://localhost:1337/products/brand/${brand}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      });

      if (response.ok) {
        // Remove the deleted brand from the brand list if the request is successful
        setEditedBrands((prevBrands) => prevBrands.filter((b) => b !== brand));
        console.log('Brand deleted successfully');
      } else {
        console.error('Failed to delete brand');
      }
    } catch (error) {
      console.error('Error deleting brand:', error.message);
    }
  };

  const handleInputChange = (e) => {
    setNewBrandValue(e.target.value);
  };

  return (
    <div className='brand-details'>
      <h3>BRANDS</h3>
      <table>
        <thead>
          <tr>
            <th>Brand</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {editedBrands.map((brand, index) => (
            <tr key={index}>
              <td>
                <input
                  type='text'
                  value={brand}
                  onChange={handleInputChange}
                />
              </td>
              <td>
                <button onClick={() => handleEditClick(brand)}>Edit</button>
                <button onClick={() => handleDeleteClick(brand)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BrandDetails;
