import React, { useState } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';

const CategoryDetails = ({ categories }) => {
    const { user } = useAuthContext();
    const [editedCategories, setEditedCategories] = useState(categories.map(() => ''));
    
    const handleEditClick = async (currentCategory, index) => {
        try {
            const response = await fetch(`http://localhost:1337/products/category/${currentCategory}/${editedCategories[index]}`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${user.token}`,
                },
                body: JSON.stringify({ newCategory: editedCategories[index] }),
            });

            if (response.ok) {
                console.log('Category updated successfully');
            } else {
                console.error('Failed to update category');
            }
        } catch (error) {
            console.error('Error updating category:', error.message);
        }
    };

    const handleDeleteClick = async (category) => {
        try {
            const response = await fetch(`http://localhost:1337/products/category/${category}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${user.token}`,
                },
            });
    
            if (response.ok) {
                // Remove the deleted category from the list
                setEditedCategories((prevCategories) => prevCategories.filter((c) => c !== category));
                console.log('Category deleted successfully');
            } else {
                console.error('Failed to delete category');
            }
        } catch (error) {
            console.error('Error deleting category:', error.message);
        }
    };


    return (
        <div className="product-details">
            <p>
                <strong>Name:</strong>
                {user && (user.role === 'administrator' || user.role === 'moderator') ? (
                    <input
                        type="text"
                        value={editedName}
                        onChange={(e) => setEditedName(e.target.value)}
                    />
                ) : (
                    <span>{product.name}</span>
                )}
            </p>
            <p>
                <strong>Brand:</strong>
                {user && (user.role === 'administrator' || user.role === 'moderator') ? (
                    <input
                        type="text"
                        value={editedBrand}
                        onChange={(e) => setEditedBrand(e.target.value)}
                    />
                ) : (
                    <span>{product.brand}</span>
                )}
            </p>
            <p>
                <strong>Category:</strong>
                {user && (user.role === 'administrator' || user.role === 'moderator') ? (
                    <input
                        type="text"
                        value={editedCategory}
                        onChange={(e) => setEditedCategory(e.target.value)}
                    />
                ) : (
                    <span>{product.category}</span>
                )}
            </p>
            <p>
                <strong>Number:</strong>
                {user && (user.role === 'administrator' || user.role === 'moderator') ? (
                    <input
                        type="number"
                        value={quantity}
                        onChange={handleQuantityChange}
                        min="1"
                        step="1"
                    />
                ) : (
                    <span>{product.number}</span>
                )}
            </p>
            <p>
                <strong>Image:</strong>
                <img src={product.image} alt="Current Product" style={{ width: 100, height: 100 }} />
            </p>
            {user && (user.role === 'administrator' || user.role === 'moderator') && (
                <p>
                    <strong>New Image:</strong>
                    {!image && (
                        <input type="file" accept="image/*" onChange={handleFileChange} />
                    )}
                    {image && (
                        <img src={URL.createObjectURL(image)} alt="Selected" style={{ width: 100, height: 100 }} />
                    )}
                </p>
            )}
    
            {user && (user.role === 'administrator' || user.role === 'moderator') && (
                <>
                    <button onClick={handleEditClick}>Save Changes</button>
                    <button onClick={handleDeleteClick}>Delete Product</button>
                </>
            )}
            {user && user.role === 'basic' && (
                <div>
                    <input 
                        type="number"
                        value={quantity}
                        onChange={handleQuantityChange}
                        min="1"
                        step="1"
                    />
                    <button onClick={handleBuyClick}>Buy</button>
                </div>
            )}
        </div>
    );
};

export default CategoryDetails;
