import React, { useState } from 'react';
import { useProductsContext } from '../hooks/useProductContext';
import { useAuthContext } from '../hooks/useAuthContext';

const ProductDetails = ({ product }) => {
  const { dispatch } = useProductsContext();
  const { user } = useAuthContext();
  const [editedName, setEditedName] = useState(product.name);
  const [editedBrand, setEditedBrand] = useState(product.brand);
  const [editedCategory, setEditedCategory] = useState(product.category);
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);

  const handleFileChange = (e) => {
    const file = e.target.files[0]; // Ensure that the file object is properly obtained
    setImage(file);
    console.log('File:', file);
    const reader = new FileReader();

    reader.onload = () => {
      // Display the selected image
      setImage(reader.result);
    };

    reader.readAsDataURL(file); // Make sure the file is read as data URL
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setError('You have to be logged in');
      return;
    }

    // Convert image to base64 format
    const base64Image = await getBase64Image(image);

    // Construct the updated product object
    const updatedProduct = {
      name: editedName,
      brand: editedBrand,
      category: editedCategory,
      image: base64Image,
    };

    try {
      const response = await fetch(`http://localhost:1337/products/${product._id}`, {
        method: 'PATCH',
        body: JSON.stringify(updatedProduct),
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
      });
      const json = await response.json();

      if (!response.ok) {
        setError(json.error);
        setEmptyFields(json.emptyFields);
      } else {
        dispatch({ type: 'UPDATE_PRODUCT', payload: json.updatedProduct });
        console.log('Product updated successfully');
      }
    } catch (error) {
      console.error('Error updating product:', error.message);
      setError('Internal server error');
    }
  };

  const getBase64Image = async (image) => {
    return new Promise((resolve, reject) => {
      if (!image) {
        resolve('');
        return;
      }

      const reader = new FileReader();
      reader.onload = () => {
        resolve(reader.result);
      };
      reader.onerror = (error) => {
        reject(error);
      };

      reader.readAsDataURL(image);
    });
  };

  return (
    <div className="product-details">
      <form onSubmit={handleSubmit}>
        <p>
          <strong>Name:</strong>
          <input type="text" value={editedName} onChange={(e) => setEditedName(e.target.value)} />
        </p>
        <p>
          <strong>Brand:</strong>
          <input type="text" value={editedBrand} onChange={(e) => setEditedBrand(e.target.value)} />
        </p>
        <p>
          <strong>Category:</strong>
          <input type="text" value={editedCategory} onChange={(e) => setEditedCategory(e.target.value)} />
        </p>
        <p>
          <strong>Image:</strong>
          <input type="file" accept="image/*" onChange={handleFileChange} />
          {image && <img src={image} alt="Selected" style={{ width: 100, height: 100 }} />}
        </p>
        <button type="submit">Save Changes</button>
        {error && <div className="error">{error}</div>}
      </form>
    </div>
  );
};

export default ProductDetails;
