import { useState } from "react"

const AddProduct = () => 
{
    const [name, setName] = useState('')
    const [brand, setBrand] = useState('')
    const [category, setCategory] = useState('')
    const [image, setImage] = useState('')
    const [number, setNumber] = useState('')
    const [error, setError] = useState(null)

    function convertToBase64(e)
    {
        console.log(e)
        var reader = new FileReader()
        reader.readAsDataURL(e.target.files[0])
        reader.onload = () =>
        {
            console.log(reader.result)
            setImage(reader.result)
        }
        reader.onerror = error =>
        {
            console.log("Error: ", error)
        }
    }

    const handleSubmit = async (e) =>
    {
        e.preventDefault()
        const product = {name, brand, category, image, number}
        const response = await fetch('http://localhost:1337/products', 
        {
            method: 'POST', 
            body: JSON.stringify(product),
            headers: 
            {
                'Content-Type': 'application/json'
            }
        })
        const json = await response.json()

        if (!response.ok)
        {
            setError(json.error)
        }
        if (response.ok)
        {
            setName('')
            setBrand('')
            setCategory('')
            setImage('')
            setNumber('')
            setError(null)
            console.log("new PRODUCT ADDED")
        }
    }

    return (
        <form onSubmit={handleSubmit}> 
            <h3> Add new product </h3>

            <label>name</label>
            <input type="text" onChange={(e) => setName(e.target.value)} value={name}/>

            <label>brand</label>
            <input type="text" onChange={(e) => setBrand(e.target.value)} value={brand}/>

            <label>category</label>
            <input type="text" onChange={(e) => setCategory(e.target.value)} value={category}/>

            <label>image</label>
            <input accept="image/*" type="file" onChange={convertToBase64} value={image}/>
            {image ? <img width={100} height={100} src={image}/> : <p>No image selected</p>}


            <label>number</label>
            <input type="number" onChange={(e) => setNumber(e.target.value)} value={number}/>
            

            <button>ADD PRODUCT</button>
            {error && <div className="error">{error}</div>}
        </form>
    )
}

export default AddProduct
